import { useEffect } from 'react';
import { useSelector } from 'react-redux';

const ScrollAnimation = (refTab = null, refList = null) => {
  const scrollTab = refTab;
  const divs = refList;
  const activeTab = useSelector(state => state.activeTab);

  useEffect(() => {
    // Normalize activeTab to lowercase to match section IDs that are lowercase
    const normalizedActiveTab = activeTab.toLowerCase();

    // Ensure the scrollTab ID matches in lowercase format
    if (scrollTab.current && scrollTab.current.id.toLowerCase() === normalizedActiveTab) {
      const componentNode = scrollTab.current;
      componentNode.scrollIntoView({ behavior: 'smooth' });
    }

    if (divs !== null) {
      divs.current.forEach((div) => {
        div.classList.add('animation');
      });

      const handlScroll = () => {
        const scrollPosition = window.scrollY;

        divs.current.forEach((div) => {
          const offsetTop = div.getBoundingClientRect().top + scrollPosition;

          if (scrollPosition >= offsetTop - (window.innerHeight / 1.5)) {
            div.classList.add('active');
          } else {
            div.classList.remove('active');
          }
        });
      };

      window.addEventListener('scroll', handlScroll);

      return () => {
        window.removeEventListener('scroll', handlScroll);
      };
    }
  }, [activeTab, divs, scrollTab]);
};

export default ScrollAnimation;
