import React, { useState } from "react";
import { useDispatch } from 'react-redux';
import { changeTabActive } from '../redux/actions';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faBars } from "@fortawesome/free-solid-svg-icons";

const NavBar = ({ activeTab }) => {
  const dispatch = useDispatch();
  const [linkNav] = useState(['Home', 'Projects', 'Contacts']);
  const [statusNav, changeStatusNav] = useState(null);

  const toggleNav = () => {
    changeStatusNav(statusNav === null ? 'active' : null);
  };

  const changeTab = (value) => {
    dispatch(changeTabActive(value)); // Update active tab in redux
    toggleNav(); // Close the navigation bar on mobile

    // Scroll to the section with the corresponding ID
    const sectionId = value.toLowerCase(); // Convert to lowercase
    const section = document.getElementById(sectionId);

    if (section) {
      section.scrollIntoView({ behavior: 'smooth' }); // Scroll smoothly to section
    }
  };

  return (
    <header>
      <div className="logo">Portfolio</div>
      <nav className={statusNav}>
        {linkNav.map((value) => (
          <span
            key={value}
            className={activeTab === value ? 'active' : ''}
            onClick={() => changeTab(value)}
          >
            {value}
          </span>
        ))}
      </nav>
      <div className="icon-bar" onClick={toggleNav}>
        <FontAwesomeIcon icon={faBars} />
      </div>
    </header>
  );
};

export default NavBar;
