import React, { useRef, useEffect } from 'react';
import ScrollAnimation from './ScrollAnimation';

function AboutMe() {
    const scrollTab = useRef(null);
    const divs = useRef(null); // Fix ref initialization

    useEffect(() => {
        ScrollAnimation(scrollTab, divs); // Call inside useEffect
    }, []); 

    return (
        <section ref={scrollTab} className='about-me'>
            <div ref={el => divs.current = el} className="content">
                <h2>About Me</h2>
                <p>
                    I am a dedicated Full Stack Developer with a passion for 
                    technology and innovation. My expertise includes front-end 
                    and back-end development, and I am always eager to learn new 
                    technologies to improve my skills and create impactful solutions.
                </p>
                <p>
                    My journey in web development has allowed me to work on 
                    various projects, from designing interactive user interfaces 
                    to developing efficient solutions. I enjoy problem-solving and collaborating on projects that make a difference.
                </p>
            </div>
        </section>
    );
}

export default AboutMe;
