import React, { useRef, useEffect } from 'react';
import { useSelector } from 'react-redux';
import ScrollAnimation from './ScrollAnimation';

function AboutMe() {
    const scrollTab = useRef(null);
    const divs = useRef([]);
    const activeTab = useSelector(state => state.activeTab); // Get activeTab here

    useEffect(() => {
        ScrollAnimation(scrollTab, divs, activeTab);
    }, [activeTab]);

    return (
        <section ref={scrollTab} className='about-me'>
            <div className="content">
                <h2>About Me</h2>
                <p ref={el => divs.current.push(el)}>
                    I am a dedicated Full Stack Developer with a passion for 
                    technology and innovation.
                </p>
                <p ref={el => divs.current.push(el)}>
                    My journey in web development has allowed me to work on 
                    various projects, from designing interactive user interfaces 
                    to developing efficient solutions.
                </p>
            </div>
        </section>
    );
}

export default AboutMe;
