import React, { useState, useRef } from 'react';
import ScrollAnimation from './ScrollAnimation';
import { FaEnvelope, FaLinkedin } from 'react-icons/fa'; // import necessary icons

function Contacts() {
  const [listContacts] = useState([

    {
      title: 'Email',
      value: 'saimariaz1422@gmail.com'
    },
    {
      title: 'LinkedIn',
      value: 'https://www.linkedin.com/in/saima-riaz-178ab7194/'
    }
  ]);
  const divs = useRef([]);
  const scrollTab = useRef();
  ScrollAnimation(scrollTab, divs);

  return (
    <section className='contacts' ref={scrollTab}>
      <div className="title" ref={(el) => el && divs.current.push(el)}>
        This is my Contacts
      </div>
      <div className="des" ref={(el) => el && divs.current.push(el)}>
        {/* 20 */}
        Feel free to reach out to me for collaboration, opportunities, or just to connect!
      </div>
      <div className="list" ref={(el) => el && divs.current.push(el)}>
        {
          listContacts.map((value, key) => (
            <div className='item' key={key}>
              <h3>{value.title}</h3>
              <div>
                {value.title === 'Email' ? (
                  <a href={`mailto:${value.value}`} target="_blank" rel="noopener noreferrer">
                    <FaEnvelope style={{ marginRight: '8px' }} />
                    {value.value}
                  </a>
                ) : value.title === 'LinkedIn' ? (
                  <a href={value.value} target="_blank" rel="noopener noreferrer">
                    <FaLinkedin style={{ marginRight: '8px' }} />
                    {value.value}
                  </a>
                ) : (
                  value.value
                )}
              </div>
            </div>
          ))
        }
      </div>
    </section>
  )
}

export default Contacts;
